	public static bool IsHiddenMove(int move) {
		for (int i=1; i<=Items.MaxValue(); i++) 
		{
			if (!IsHiddenMachnine()) {
				continue;
			}
			int atk = GetMachine(i);
			if (move == atk) {
				return true;
			}
		}
		return false;
	}

